    private void oreoRBActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_oreoRBActionPerformed
        // changing radio buttons to select only one

        if (oreoRB.isSelected()) {
            cokeRB.setSelected(false);
            nutellaRB.setSelected(false);
            chocolateMilkaRB.setSelected(false);
            pepsiRB.setSelected(false);
            waterRB.setSelected(false);
        }
    }

