    private void purchaseBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_purchaseBtnActionPerformed
        // does the payment if we have enough money
        
        double money = 0;
        
        money = Double.parseDouble(MoneyTF.getText());
        
        if(oreoRB.isSelected()){
            money = money - 1.25;
        }else if(waterRB.isSelected()){
            money = money - 0.80;
        }else if(pepsiRB.isSelected()){
            money = money - 1.80;
        }else if(nutellaRB.isSelected()){
            money = money - 2.00;
        }else if(chocolateMilkaRB.isSelected()){
            money = money - 0.99;
        }else if(cokeRB.isSelected()){
            money = money - 1.50;
        }
        
        if(money < 0){
            JOptionPane.showMessageDialog(rootPane, "Sorry, you don't have Sufficient Money");
        }else{
            changeTF.setText(Double.toString(money));
        }
    }

